mapper.select:

select * from
(
	select rownum rn, orderNum, id, orderStatus, orderDate
	from oorder
	where id = #{id}
	and rownum <= #{pageNum} * #{amount}


	and orderNum like '%' || #{keyword} || '%'	


	order by orderDate desc
)
where rn > (#{pageNum}-1) * #{amount}

select count(*) from oorder
where id=#{id}

and orderNum like '%' || #{keyword} || '%'
