mapper.select:


	    select * from(select /*+INDEX_DESC(product 인덱스ID)*/ rownum as rn, prodCode, prodName,
	            (select cateName from category where product.cateCode = category.cateCode) cateName,cateCode, price, prodInfo, stock  
	        from product
	        where 






	        rownum <= #{pageNum} * #{amount} 
        	)
        where rn > (#{pageNum} -1) * #{amount}
